(function() {
	// return the value to use for Parameter 1
	if(current.task.sys_class_name == 'x_277835_schdl_it_individual_task')
		return current.task.user.email;
	else if(current.task.sys_class_name == 'x_277835_schdl_it_group_task'){
		var listEmail = [];
		var grGroupMember = new GlideRecord('sys_user_grmember');
		grGroupMember.addQuery('group', current.task.group);
		grGroupMember.addQuery('user.active', 'true');
		grGroupMember.query();
		while(grGroupMember.next()){
			listEmail.push(grGroupMember.email);
		}
		
		return listEmail.join(',');
	}
}());

